    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try{
            other.item = txt1.getText();
            other.price = Integer.parseInt(txt2.getText());
            other.item1 = txt3.getText();
            other.price1 = Integer.parseInt(txt4.getText());
            
            //label enumeration for price and item name slightly got changed no worries.
            other.item2 = txt6.getText();
            other.price2 = Integer.parseInt(txt5.getText());
            other.item3 = txt8.getText();
            other.price3 = Integer.parseInt(txt7.getText());
            this.dispose();
            roll3 second = new roll3();
            second.setVisible(true);
        }
        catch(Exception e)
        {
            lbl1.setText("Enter valid fields (integer only allowed");
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

